import React from "react";
import { useForm, Controller } from "react-hook-form";
import CheckboxInput from "components/FormElements/CheckboxInput";

import {BodyContainer, FormBody} from "styles/pages/CheckList";

const CheckList = () => {

  const {
    setValue,
    handleSubmit,
    formState: { errors },
    control,
  } = useForm({
    mode: "onSubmit",
    reValidateMode: "onBlur",
    // resolver: yupResolver(VALIDATION_SCHEMA),
    shouldFocusError: true,
  });

    const taskList = ['Task 1','Task 2','Task 3','Task 4','Task 5'];
    const subList = taskList.map((index,task)=>{
        return <li key={index}>
            <Controller
                name={"subTask"+index}
                control={control}
                render={({ field }) => (
                    <CheckboxInput
                        className="checkBox"
                        label={task}
                        {...field}
                    />
                )}
            />
        </li>;
    });

    const lists = taskList.map((index,task)=>{
        console.log("task==",task)
        return <li key={index}>
                    <Controller
                        name={"task"+index}
                        control={control}
                        render={({ field }) => (
                            <CheckboxInput
                                className="checkBox"
                                label={task}
                                {...field}
                            />
                        )}
                    />
                <ul>
                 {subList}
                </ul>
            </li>;
    });
    const formFields = () => {
        return (
            <FormBody>
                <ul>
                    {lists}
                </ul>
            </FormBody>
        );
    };

    return (
        <>
          <BodyContainer>{formFields()}</BodyContainer>         
        </>
    );
}
export default CheckList;
