import styled from 'styled-components'

export const BodyContainer = styled.div`
  padding-top: 29px;
  padding-bottom: 29px;
  width:100%;
  /* display:flex; */
  form{
    display: flex;
    padding-left: 20px;
  }
  .submitBtn{
    padding-top: 12px;
    padding-left: 10px;
  }
`
export const FormBody = styled.div`
  padding-top: 29px;
  padding-bottom: 29px;
  width: 100%;  
  display: flex;
  /* padding-left: 20px; */
`
export const AddTask = styled.div` 
  from{
    display: flex;
    padding-left: 15px;
  }
`

export const TaskList = styled.div`
    /* background-color: grey; */
    text-align: left;
    padding-left: 20px;
    padding-right: 20px;
   >div.checkbox-field{
      background-color: grey;
      padding-left: 20px;
   }
   .checkbox-container {
    font-size: 22px;
    font-family: 'poppinsSemibold';
    span {
      height: 30px !important;
      width: 30px !important;
    }
  }
  .checkMark:after {
    width: 13px !important;
    height: 17px !important;
  }

`
